mapper.select:
SELECT postId, userId, title, content, image, regDate, cID
FROM tbl_post
WHERE postId=#{postId}

SELECT postId, userId, title, content, image, regDate, cID
FROM tbl_post
WHERE postId=#{postId} AND userId=#{userId}

mapper.update:
UPDATE tbl_post
SET title=#{title}, content=#{content}, image=#{image}, cID=#{cID}
WHERE postId=#{postId}

mapper.select:
SELECT image
FROM tbl_post
WHERE postId=#{postId}
